package org.launchcode.codingevents.controllers;

import org.launchcode.codingevents.models.Event;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.RequestParam;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

@Controller
@RequestMapping("events")
public class EventController {

    private static Map<String, Event> events = new HashMap<>();

    static {
        events.put("Menteaship", new Event("Menteaship", "A fun meetup for connecting with mentors", "data:image/jpeg;base64,"));
        events.put("Code With Pride", new Event("Code With Pride", "A fun meetup sponsored by LaunchCode", "data:image/jpeg;base64,"));
        events.put("Javascripty", new Event("Javascripty", "An imaginary meetup for Javascript developers", "data:image/png;base64,"));
    }

    @GetMapping
    public String displayAllEvents(Model model) {
        model.addAttribute("title", "All Events");
        model.addAttribute("events", events.values());
        return "events/index";
    }

    @GetMapping("create")
    public String displayCreateEventForm(Model model) {
        model.addAttribute("title", "Create Event");
        return "events/create";
    }

    @PostMapping("create")
    public String processCreateEventForm(@RequestParam String eventName, @RequestParam String description, @RequestParam String imageUrl) {
        Event newEvent = new Event(eventName, description, imageUrl);
        events.put(eventName, newEvent);
        return "redirect:/events";
    }
}